import React from "react";
import { Link } from "react-router-dom";

import "./styles/Badges.css";
import confLogo from "../images/badge-header.svg";

import BadgesList from "../components/BadgesList";

class Badges extends React.Component {
  constructor(props) {
    super(props);
    console.log("1. constructor()");

    this.state = {
      data: []
    };
  }

  componentDidMount() {
    console.log("3. componentDidMount()");

    setTimeout(() => {
      this.setState();
      data[
        ({
          id: "SUUPERGIRL",
          firstName: "Vania",
          lastName: "Bachur",
          webPage: "vaniabachur.com",
          jobTitle: "Me pagan por dibujar.",
          twitter: "suupergirl",
          avatarUrl:
            "https://pbs.twimg.com/profile_images/958526500319514624/oGm0Ldv7_400x400.jpg"
        },
        {
          id: "PACASSO",
          firstName: "Francisco",
          lastName: "Almaraz",
          webPage: "drnetas.com",
          jobTitle: "Le humoristé.",
          twitter: "DrNetas",
          avatarUrl:
            "https://pbs.twimg.com/profile_images/545333813438459904/bniO9an8_400x400.png"
        },
        {
          id: "PON",
          firstName: "Alfonso",
          lastName: "Cervantes",
          webPage: "poncervantes.com",
          jobTitle: "Ilustrador, pokemon trainer.",
          twitter: "pon_cervantes",
          avatarUrl:
            "https://pbs.twimg.com/profile_images/1103122083469418496/TV1Pw4KB_400x400.jpg"
        },
        {
          id: "MAREOFLORES",
          firstName: "Mario",
          lastName: "Flores",
          webPage: "mareoflores.com",
          jobTitle: "Se ilustra ajeno.",
          twitter: "mareoflores",
          avatarUrl:
            "https://pbs.twimg.com/profile_images/1123454507478921220/laDiTkL5_400x400.png"
        },
        {
          id: "GRAND CHAMACO",
          firstName: "-------",
          lastName: "-------",
          webPage: "grandchamaco.com",
          jobTitle: "I draw tweets.",
          twitter: "SeDibujanTweets",
          avatarUrl:
            "https://pbs.twimg.com/profile_images/739878113362907136/8fByGWPh_400x400.jpg"
        })
      ];
    }, 3000);
  }

  render() {
    console.log("2. render()");
    return (
      <React.Fragment>
        <div className="Badges">
          <div className="Badges__hero">
            <div className="Badges__container">
              <img
                className="Badges_conf-logo"
                src={confLogo}
                alt="Conf Logo"
              />
            </div>
          </div>
        </div>
        <div className="Badge_container">
          <div className="Badges__buttons">
            <Link to="/badges/new" className="btn btn-primary">
              Ilustradores Mexicanos
            </Link>
          </div>
          <div className="Badges__list">
            <div className="Badges__container">
              <BadgesList badges={this.state.data} />
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Badges;
